import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import SearchBar from './components/SearchBar';
import TenderList from './components/TenderList';
import { searchTenders } from './services/api';

function App() {
  const [tabs, setTabs] = useState([
    { id: 1, searchTerm: 'Тестовый поиск', tenders: [], loading: false }
  ]);
  const [activeTabId, setActiveTabId] = useState(1);

  const activeTab = tabs.find(tab => tab.id === activeTabId) || tabs[0];

  const fetchTenders = useCallback(async (query, tabId) => {
    setTabs(prevTabs => 
      prevTabs.map(tab => 
        tab.id === tabId ? { ...tab, loading: true } : tab
      )
    );

    try {
      const data = await searchTenders(query);

      setTabs(prevTabs => 
        prevTabs.map(tab => 
          tab.id === tabId ? { ...tab, tenders: data, loading: false } : tab
        )
      );
    } catch (error) {
      console.error('Failed to fetch tenders:', error);
      setTabs(prevTabs => 
        prevTabs.map(tab => 
          tab.id === tabId ? { ...tab, loading: false } : tab
        )
      );
    }
  }, []);

  useEffect(() => {
    if (activeTab.searchTerm && activeTab.tenders.length === 0 && !activeTab.loading) {
      fetchTenders(activeTab.searchTerm, activeTabId);
    }
  }, [activeTabId, activeTab, fetchTenders]);

  const handleSearch = useCallback((term, tabId = activeTabId) => {
    setTabs(prevTabs => 
      prevTabs.map(tab => 
        tab.id === tabId ? { ...tab, searchTerm: term } : tab
      )
    );
    fetchTenders(term, tabId);
  }, [activeTabId, fetchTenders]);

  const handleClearSearch = useCallback((tabId) => {
    setTabs(prevTabs => 
      prevTabs.map(tab => 
        tab.id === tabId ? { ...tab, searchTerm: '', tenders: [] } : tab
      )
    );
  }, []);

  const addNewTab = useCallback(() => {
    const newTabId = Math.max(...tabs.map(tab => tab.id), 0) + 1;
    setTabs(prevTabs => [...prevTabs, { 
      id: newTabId, 
      searchTerm: 'Новый поиск', 
      tenders: [], 
      loading: false 
    }]);
    setActiveTabId(newTabId);
  }, [tabs]);

  const closeTab = useCallback((tabId) => {
    if (tabs.length <= 1) return;

    const newTabs = tabs.filter(tab => tab.id !== tabId);
    setTabs(newTabs);

    if (tabId === activeTabId) {
      setActiveTabId(newTabs[newTabs.length - 1].id);
    }
  }, [tabs, activeTabId]);

  return (
    <div className="app">
      <SearchBar 
        tabs={tabs}
        activeTabId={activeTabId}
        onTabChange={setActiveTabId}
        onSearch={handleSearch}
        onClearSearch={() => handleClearSearch(activeTabId)}
        onAddTab={addNewTab}
        onCloseTab={closeTab}
      />
      {activeTab.loading ? (
        <div className="loading">Загрузка...</div>
      ) : (
        <TenderList tenders={activeTab.tenders} />
      )}
    </div>
  );
}

export default App;
