import React, { useState, useEffect } from 'react';
import './SearchBar.css';

const SearchBar = ({ 
  tabs, 
  activeTabId, 
  onTabChange, 
  onSearch, 
  onClearSearch, 
  onAddTab,
  onCloseTab 
}) => {
  const activeTab = tabs.find(tab => tab.id === activeTabId) || tabs[0];
  const [inputValue, setInputValue] = useState('');

  // Синхронизируем inputValue с изменением активной вкладки
  useEffect(() => {
    if (activeTab) {
      setInputValue(activeTab.searchTerm || '');
    }
  }, [activeTab]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputValue.trim()) {
      onSearch(inputValue);
    }
  };

  const handleClear = () => {
    setInputValue('');
    onClearSearch();
  };

  return (
    <div className="search-container">
      <div className="tabs-container">
        {tabs.map(tab => (
          <div 
            key={tab.id} 
            className={`tab ${tab.id === activeTabId ? 'active-tab' : ''}`}
            onClick={() => onTabChange(tab.id)}
          >
            <span className="tab-text">
              {tab.searchTerm ? tab.searchTerm : 'Без названия'}
            </span>
            {tabs.length > 1 && (
              <button 
                className="close-tab-button" 
                onClick={(e) => {
                  e.stopPropagation();
                  onCloseTab(tab.id);
                }}
              >
                ×
              </button>
            )}
          </div>
        ))}
        <button className="add-tab-button" onClick={onAddTab}>+</button>
      </div>

      <form className="search-bar" onSubmit={handleSubmit}>
        <div className="search-input-container">
          <input
            type="text"
            className="search-input"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="Введите запрос..."
          />
          {inputValue && (
            <button 
              type="button" 
              className="clear-button" 
              onClick={handleClear}
            >
              ×
            </button>
          )}
        </div>
        <button type="submit" className="search-button">Поиск</button>
      </form>
    </div>
  );
};

export default SearchBar;
