import React from 'react';
import './SearchBar.css';

const SearchBar = ({ 
  tabs, 
  activeTabId, 
  onTabChange, 
  onSearch, 
  onClearSearch, 
  onAddTab,
  onCloseTab 
}) => {
  // Найдем активную вкладку
  const activeTab = tabs.find(tab => tab.id === activeTabId) || tabs[0];

  return (
    <div className="search-container">
      <div className="tabs-container">
        {tabs.map(tab => (
          <div 
            key={tab.id} 
            className={`tab ${tab.id === activeTabId ? 'active-tab' : ''}`}
            onClick={() => onTabChange(tab.id)}
          >
            <span className="tab-text">{tab.searchTerm || 'Пустой поиск'}</span>
            {tabs.length > 1 && (
              <button 
                className="close-tab-button" 
                onClick={(e) => {
                  e.stopPropagation();
                  onCloseTab(tab.id);
                }}
              >
                ×
              </button>
            )}
          </div>
        ))}
        <button className="add-tab-button" onClick={onAddTab}>+</button>
      </div>

      <div className="search-bar">
        <div className="search-input-container">
          <span className="search-term">{activeTab.searchTerm}</span>
          {activeTab.searchTerm && (
            <button className="clear-button" onClick={onClearSearch}>
              ×
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default SearchBar;
